const API_URL = 'http://localhost:5000'; // Replace with your server URL

// Fetch recent items from the server
function fetchItems() {
  fetch(`${API_URL}/items`)
    .then((response) => response.json())
    .then((data) => {
      const items = data.items;
      displayItems(items);
    })
    .catch((error) => {
      console.error('Error:', error);
    });
}

// Display items on the page
function displayItems(items) {
  const itemsList = document.getElementById('items-list');
  itemsList.innerHTML = '';

  items.forEach((item) => {
    const li = document.createElement('li');
    li.textContent = item;
    itemsList.appendChild(li);
  });
}

// Insert a new item
function insertItem() {
  const itemInput = document.getElementById('item-input');
  const item = itemInput.value.trim();

  if (item !== '') {
    fetch(`${API_URL}/insert`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ item: item }),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log(data.message);
        itemInput.value = '';
        fetchItems(); // Refresh the items list
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  }
}

// Generate a suggestion
function generateSuggestion() {
  fetch(`${API_URL}/suggestions`)
    .then((response) => response.json())
    .then((data) => {
      const suggestion = data.suggestion;
      displaySuggestion(suggestion);
    })
    .catch((error) => {
      console.error('Error:', error);
    });
}

// Display the suggestion on the page
function displaySuggestion(suggestion) {
  const suggestionText = document.getElementById('suggestion-text');
  suggestionText.textContent = suggestion;
}

// Delete an item
function deleteItem(item) {
  fetch(`${API_URL}/delete`, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({ item: item }),
  })
    .then((response) => response.json())
    .then((data) => {
      console.log(data.message);
      fetchItems(); // Refresh the items list
    })
    .catch((error) => {
      console.error('Error:', error);
    });
}

// Update an item
function updateItem() {
  const itemToUpdate = document.getElementById('item-to-update').value.trim();
  const updatedText = document.getElementById('updated-text').value.trim();

  if (itemToUpdate !== '' && updatedText !== '') {
    fetch(`${API_URL}/update`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ item: itemToUpdate, updatedText: updatedText }),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log(data.message);
        fetchItems(); // Refresh the items list
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  }
}

// Event listeners
document.getElementById('insert-btn').addEventListener('click', insertItem);
document.getElementById('suggest-btn').addEventListener('click', generateSuggestion);
document.getElementById('update-btn').addEventListener('click', updateItem);

// Fetch recent items on page load
fetchItems();
