// Table.js
import React, { useState } from "react";
import "./Table.css";
import { BsFillTrashFill, BsFillPencilFill } from "react-icons/bs";

export const Table = ({ items, onUpdateItem }) => {
  const [editingItem, setEditingItem] = useState(null);
  const [updatedItemText, setUpdatedItemText] = useState("");

  const handleUpdateClick = (item) => {
    setEditingItem(item);
    setUpdatedItemText(item);
  };

  const handleConfirmUpdate = () => {
    onUpdateItem(editingItem, updatedItemText);
    setEditingItem(null);
    setUpdatedItemText("");
  };

  const handleCancelUpdate = () => {
    setEditingItem(null);
    setUpdatedItemText("");
  };

  const handleItemChange = (event) => {
    setUpdatedItemText(event.target.value);
  };

  return (
    <div className="table-wrapper">
      <table className="table">
        <thead>
          <tr>
            <th>Page</th>
            <th className="expand">Description</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {items.map((item, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              <td>
                {editingItem === item ? (
                  <input
                    type="text"
                    value={updatedItemText}
                    onChange={handleItemChange}
                  />
                ) : (
                  item
                )}
              </td>
              <td>
                <span className="label label-draft">Draft</span>
              </td>
              <td>
                <span className="actions">
                  <BsFillTrashFill className="delete-btn" />
                  {editingItem === item ? (
                    <>
                      <BsFillPencilFill
                        className="update-btn"
                        onClick={handleConfirmUpdate}
                      />
                      <BsFillPencilFill
                        className="cancel-btn"
                        onClick={handleCancelUpdate}
                      />
                    </>
                  ) : (
                    <BsFillPencilFill
                      className="update-btn"
                      onClick={() => handleUpdateClick(item)}
                    />
                  )}
                </span>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};
