import React, { useState, useEffect } from 'react';
import './App.css';
import { Table } from "./components/Table";

function App() {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [inputItem, setInputItem] = useState('');
  const [suggestion, setSuggestion] = useState('');


  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await fetch('http://localhost:5000/items', {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
      });
      const data = await response.json();
      setItems(data.items);
      setLoading(false);
    } catch (error) {
      console.error(error);
      setLoading(false);
    }
  };

  const handleInsertItem = async () => {
    try {
      const response = await fetch('http://localhost:5000/insert', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ item: inputItem }),
      });
      const data = await response.json();
      console.log(data); // Log the response data
      setInputItem('');
      fetchData(); // Fetch updated data after insertion
    } catch (error) {
      console.error(error);
    }
  };


  const handleGenerateSuggestion = async () => {
    try {
      const response = await fetch('http://localhost:5000/suggestions', {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
      });
      const data = await response.json();
      setSuggestion(data.suggestion);
    } catch (error) {
      console.error(error);
    }
  };


  return (
    <div>
      {loading ? (
        <p>Loading data...</p>
      ) : (
        <div>
          <h1>Recent Search History:</h1>
          <Table items={items} />
        </div>
      )}
      <div>
        <button onClick={handleGenerateSuggestion}>Generate Suggestion</button>
      </div>
      {suggestion && (
        <div>
          <h2>Suggested Item:</h2>
          <p>{suggestion}</p>
        </div>
      )}

    </div>
  );
}

export default App;
