import React, { useState } from "react";
import "./Table.css";
import { BsFillTrashFill, BsFillPencilFill, BsFillXCircleFill, BsFillCheckCircleFill } from "react-icons/bs";

export const Table = ({ items, onUpdateItem }) => {
  const [editItem, setEditItem] = useState(null);
  const [updatedText, setUpdatedText] = useState("");
  const [hoveredRow, setHoveredRow] = useState(null);

  const handleUpdateClick = (item) => {
    setEditItem(item);
    setUpdatedText(item); // Remove this line if you want to use a separate input field for editing
  };






  const handleConfirmUpdate = async (item) => {
    try {
      const response = await fetch("http://localhost:5000/update", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ id: item.id, updatedText }),
      });
      const data = await response.json();
      console.log(data); // Log the response data
      setEditItem(null);
      setUpdatedText("");
      onUpdateItem(item.id); // Update the item in the UI
    } catch (error) {
      console.error(error);
    }
  };



  const handleDisregardUpdate = (item) => {
    setEditItem(null);
    setUpdatedText("");
  };

  const handleInputChange = (event) => {
    setUpdatedText(event.target.value);
  };

  return (
    <div className="table-wrapper">
      <table className="table">
        <thead>
          <tr>
            <th>#</th>
            <th className="expand">Item</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {items.map((item, index) => (
            <tr
              key={index}
              className={index === hoveredRow ? "highlighted-row" : ""}
              onMouseEnter={() => setHoveredRow(index)}
              onMouseLeave={() => setHoveredRow(null)}
            >
              <td>{index + 1}</td>
              <td>
                {editItem === item ? (
                  <input
                    type="text"
                    value={updatedText}
                    onChange={handleInputChange}
                  />
                ) : (
                  item
                )}
              </td>
              <td>
                <span className="actions">
                  <BsFillTrashFill
                    className="delete-btn"
                    onClick={() => handleDeleteClick(item)}
                  />
                  {editItem === item ? (
                    <>
                      <BsFillCheckCircleFill
                        className="confirm-btn"
                        onClick={() => handleConfirmUpdate(item)}
                      />
                      <BsFillXCircleFill
                        className="disregard-btn"
                        onClick={() => handleDisregardUpdate(item)}
                      />
                    </>
                  ) : (
                    <BsFillPencilFill
                      className="update-btn"
                      onClick={() => handleUpdateClick(item)}
                    />
                  )}
                </span>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};
