import React, { useState, useEffect } from 'react';
import './App.css';
import { Table } from "./components/Table";
import Modal from 'react-modal';
import Popup from "./components/popup"

function App() {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [inputItem, setInputItem] = useState('');
  const [suggestion, setSuggestion] = useState('');
  const [modal, setModal] = useState(false);
  const [buttonPopup, setButtonPopup] = useState(false)

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await fetch('http://localhost:5000/items', {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
      });
      const data = await response.json();
      setItems(data.items);
      setLoading(false);
    } catch (error) {
      console.error(error);
      setLoading(false);
    }
  };

  const handleInputChange = (event) => {
    setInputItem(event.target.value);
  };

  const handleInsertItem = async () => {
    try {
      const response = await fetch('http://localhost:5000/insert', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ item: inputItem }),
      });
      const data = await response.json();
      console.log(data); // Log the response data
      setInputItem('');
      fetchData(); // Fetch updated data after insertion
    } catch (error) {
      console.error(error);
    }
  };

  const handleGenerateSuggestion = async () => {
    try {
      const response = await fetch('http://localhost:5000/suggestions', {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
      });
      const data = await response.json();
      setSuggestion(data.suggestion);
    } catch (error) {
      console.error(error);
    }
  };


  const handleUpdateItem = async (item) => {
    try {
      const response = await fetch('http://localhost:5000/delete', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ item }),
      });
      const data = await response.json();
      console.log(data); // Log the response data
      fetchData(); // Fetch updated data after deletion
    } catch (error) {
      console.error(error);
    }
  };

  const toggleModal = () => {
    setModal(!modal)
  }


  return (
    <div className="App">
      {loading ? (
        <p>Loading data...</p>
      ) : (
        <div>
          <h1 className="header">Recent Search History</h1>
          <div className="suggestion">
            <input
              className="input-field"
              type="text"
              value={inputItem}
              onChange={handleInputChange}
            />
            <button className="insert-button" onClick={handleInsertItem}>
              Insert
            </button>
            <button className="generate-button" onClick={handleGenerateSuggestion}>
              Generate Suggestion
            </button>
          </div>
          <Table items={items} onUpdateItem={handleUpdateItem} />
          {buttonPopup && (
            <div className="suggested">
              <Popup trigger={buttonPopup} setTrigger={setButtonPopup}>
                <h2>Suggested Item:</h2>
                <p>{suggestion}</p>
              </Popup>
            </div>
          )}
        </div>
      )}
    </div>
  );

}

export default App;
