import React, { useState } from "react";
import "./Table.css";
import { BsFillTrashFill, BsFillPencilFill, BsFillXCircleFill, BsFillCheckCircleFill } from "react-icons/bs";

export const Table = ({ items, onUpdateItem }) => {
  const [editItem, setEditItem] = useState(null);
  const [updatedText, setUpdatedText] = useState("");
  const [hoveredRow, setHoveredRow] = useState(null);
  const [displayedIndices, setDisplayedIndices] = useState([]); // Array to store displayed indices

  const handleUpdateClick = (item) => {
    setEditItem(item);
    setUpdatedText(item);
  };

  const handleDeleteClick = async (index) => {
    const item = items[index]; // Get the item based on the index

    try {
      const response = await fetch("https://crud7-4obh.onrender.com/delete", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ item }),
      });
      const data = await response.json();
      console.log(data); // Log the response data
      onUpdateItem(index); // Remove the item from the UI by passing the index
    } catch (error) {
      console.error(error);
    }
  };


  const handleConfirmUpdate = async (item) => {
    try {
      const response = await fetch("https://crud7-4obh.onrender.com/update", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ item, updatedText }),
      });
      const data = await response.json();
      console.log(data); // Log the response data
      setEditItem(null);
      setUpdatedText("");
      onUpdateItem(item); // Update the item in the UI
    } catch (error) {
      console.error(error);
    }
  };

  const handleDisregardUpdate = (item) => {
    setEditItem(null);
    setUpdatedText("");
  };

  const handleInputChange = (event) => {
    setUpdatedText(event.target.value);
  };

  // Function to update the displayed indices array
  const updateDisplayedIndices = (index) => {
    if (!displayedIndices.includes(index)) {
      setDisplayedIndices([...displayedIndices, index]);
    }
  };

  return (
    <div className="table-wrapper">
      <table className="table">
        <thead>
          <tr>
            <th>#</th>
            <th className="expand">Item</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {items.map((item, index) => {
            // Check if the item is currently being displayed
            const isDisplayed = displayedIndices.includes(index);

            return (
              <tr
                key={index}
                className={index === hoveredRow ? "highlighted-row" : ""}
                onMouseEnter={() => {
                  setHoveredRow(index);
                  updateDisplayedIndices(index);
                }}
                onMouseLeave={() => setHoveredRow(null)}
              >
                <td>{index + 1}</td>
                <td>
                  {editItem === item ? (
                    <input
                      type="text"
                      value={updatedText}
                      onChange={handleInputChange}
                    />
                  ) : (
                    item
                  )}
                </td>
                <td>
                  <span className="actions">
                  <BsFillTrashFill
  className="delete-btn"
  onClick={() => handleDeleteClick(index)} // Pass the index to handleDeleteClick
/>

                    {editItem === item ? (
                      <>
                        <BsFillCheckCircleFill
                          className="confirm-btn"
                          onClick={() => handleConfirmUpdate(item)}
                        />
                        <BsFillXCircleFill
                          className="disregard-btn"
                          onClick={() => handleDisregardUpdate(item)}
                        />
                      </>
                    ) : (
                      <BsFillPencilFill
                        className="update-btn"
                        onClick={() => handleUpdateClick(item)}
                      />
                    )}
                  </span>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
};
