import React from 'react';
import './Popup.css'
import { useState } from 'react';

function Popup(props) {
    return (props.trigger) ? (
        <div className='popup'>
            <div className='popup-inner'>
                <button className='close-btn' onClick={props.closePopup}>close</button>
                { props.children }
            </div>

        </div>
    ) : "";
}
export default Popup
