import React, { useState } from "react";
import "./Table.css";
import { BsFillTrashFill, BsFillPencilFill } from "react-icons/bs";

export const Table = ({ items, onUpdateItem }) => {
  const [editItem, setEditItem] = useState(null);
  const [updatedText, setUpdatedText] = useState("");

  const handleUpdateClick = (item) => {
    setEditItem(item);
    setUpdatedText(item);
  };

  const handleDeleteClick = async (item) => {
    try {
      const response = await fetch("http://localhost:5000/delete", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ item }),
      });
      const data = await response.json();
      console.log(data); // Log the response data
      onUpdateItem(item); // Remove the item from the UI
    } catch (error) {
      console.error(error);
    }
  };

  const handleConfirmUpdate = async () => {
    try {
      const response = await fetch("http://localhost:5000/update", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ item: editItem, updatedText }),
      });
      const data = await response.json();
      console.log(data); // Log the response data
      setEditItem(null);
      setUpdatedText("");
      onUpdateItem(editItem); // Update the item in the UI
    } catch (error) {
      console.error(error);
    }
  };

  const handleDisregardUpdate = () => {
    setEditItem(null);
    setUpdatedText("");
  };

  const handleInputChange = (event) => {
    setUpdatedText(event.target.value);
  };

  return (
    <div className="table-wrapper">
      <table className="table">
        <thead>
          <tr>
            <th>Page</th>
            <th className="expand">Description</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {items.map((item, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              <td>
                {editItem === item ? (
                  <input
                    type="text"
                    value={updatedText}
                    onChange={handleInputChange}
                  />
                ) : (
                  item
                )}
              </td>
              <td>
                <span className="label label-draft">Draft</span>
              </td>
              <td>
                <span className="actions">
                  <BsFillTrashFill
                    className="delete-btn"
                    onClick={() => handleDeleteClick(item)}
                  />
                  {editItem === item ? (
                    <>
                      <button onClick={handleConfirmUpdate}>Confirm</button>

                      <button onClick={handleDisregardUpdate}>Disregard</button>
                    </>
                  ) : (
                    <BsFillPencilFill
                      className="update-btn"
                      onClick={() => handleUpdateClick(item)}
                    />
                  )}
                </span>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};
